CREATE PROCEDURE [dbo].[Delete_GoalById]
	@id int 
AS

Begin
	set nocount on;

	Insert into Goal (GoalName,GoalDescription, HabitId, StartDate,TargetPeriod,Active) values (@GoalName,@GoalDescription,@HabitId, @StartDate, @TargetPeriod,@Active);

	Select  @Id= SCOPE_IDENTITY();
END

CREATE PROCEDURE [dbo].[spAdd_Goal]
	@GoalName nvarchar(50),
	@GoalDescription nvarchar(400),
	@HabitId int,
	@StartDate datetime2(7),
	@TargetPeriod datetime2(7),
	@Active bit,
	@Id int=0 output
AS

BEGIN
	set nocount on;
	Delete From Progress where GoalId IN (Select Id from Goal where HabitId = @Id);
	Delete from Goal where HabitId= @Id;
	Delete from Habit where Id = @Id;
END
RETURN 0

CREATE PROCEDURE [dbo].[spDelete_HabitById]
	@Id int
AS

Begin
set nocount on;
	SELECT [Id], [HabitId], [GoalName], [GoalDescription], [StartDate], [TargetPeriod] , Active
	from dbo.Goal where HabitId = @Id;

End
RETURN 0

